insert into paquete (idpaq, nombre, detalle, precio, idagencia, imgpaq, cantidadp, tipopaq) values (10, 'COPACABANA E ISLA DEL SOL', '¡Visita Copacabana! un lugar hermoso e imperdible para conocer atractivos turísticos únicos en el Mundo!', 980, 13, 'paquete10.jpg', 30, 3), (12, 'Año Nuevo en Ecolodge La Estancia', '¡Recibe el 2021 con nuevas energías en la paradisíaca Isla del Sol!', 2219, 14, 'paquete12.jpg', 40, 2), (13, 'TOUR A LA ISLA DEL SOL EN 1 DÍA DESDE COPACABANA', 'Conoce la Isla del Sol, en donde se encuentra los restos Arqueológicos dedicados al culto del sol o Inti. Ven con nosotros y descubre el origen del imperio incaico en el Lago Navegable más alto del mundo. ', 357, 13, 'paquete13.jpg', 40, 1), (14, 'TOUR ISLA DEL SOL Y LA LUNA 2D/1N', 'Las Islas del Sol y la Luna: La Isla del Sol o isla Titicaca es una isla boliviana situada en el lago Titicaca, es la isla más grande del lago. Por otra parte, la Isla de la Luna, es una pequeña y escarpada isla que en la época del Imperio inca contaba con un templo denominado Iñakuyu o Palacio de las vírgenes de sol, donde habitaban las denominadas vírgenes del sol. ', 875, 14, 'paquete14.jpg', 30, 3), (15, 'KAYAK A ORILLAS DEL LAGO TITICACA 3 HORAS.', 'Lago Titicaca es el lago navegable más alto del mundo y es un privilegio hacer el deporte de Kayak en él. Este tour es dirigido para todas las personas que deseen experimentar un pequeño paseo en kayak, sin necesidad de tener experiencia en el deporte mencionado; además se disfruta de la diversidad biológica y la utilización sostenible de los recursos de flora, siendo la totora la planta más famosa, y fauna silvestre (anfibios, reptiles, mamíferos y aves).', 280, 13, 'paquete15.jpg', 25, 1), (16, 'KAYAK Y AVISTAMIENTO DE AVES EN EL LAGO TITICACA 4 HORAS.', 'La Reserva Nacional del Titicaca, forma parte del Sistema Nacional de Áreas Naturales Protegidas por el estado peruano (SINANPE) es un área destinada a la conservación de la diversidad biológica y la utilización sostenible de los recursos de flora, siendo la totora la planta más famosa y fauna (anfibios, reptiles, mamíferos y aves).', 371, 14, 'paquete16.jpg', 28, 1), (17, 'TOUR PRIVADO A LAS ISLAS FLOTANTES LOS UROS EN 3 HORAS', 'A 5 km. de la ciudad de Puno, en el Lago Titicaca, se encuentran las Islas Flotantes de los Uros, en las más de 80 islas se sigue manteniendo la tradición de la pesca artesanal, especialmente del carachi y el pejerrey; también se dedican a la caza de aves silvestres y a la recolección de huevos de pato. A comienzos del siglo XXI han dirigido sus actividades al turismo y se han convertido en un punto obligado en el recorrido de los turistas que pasan por la ciudad de Puno.', 224, 13, 'paquete17.jpg', 28, 1), (18, ' Excursión a Copacabana e Isla del Sol desde La Paz', 'Disfruta del famoso Lago Titicaca con este tour por Copacabana e Isla del Sol.¡Es una experiencia completa e imperdible!', 784, 14, 'paquete18.jpg', 30, 1), (19, 'ZIPLINE COPACABANA', ' ZIPLINE Copacabana es una agencia de servicios de deporte extremo y aventura, que brinda al visitante una manera de vivir y disfrutar de nuevas experiencias en Copacabana.', 250, 14, 'paquete19.jpg', 25, 1), (20, ' ISLA SUNATA – COPACABANA ', 'Este fin de semana viaja con nosotros!!Sábado 12 de diciembre : COPACABANA-ISLA SUNATA',210, 13, 'paquete20.jpg', 35, 1), (21, 'Copacabana, Isla del Sol', 'En este tour Full Day, podrá conocer uno de los destinos más representativos de La Paz y Bolivia, inicialmente la población de Copacabana que se destaca por ser un centro histórico cultural en Bolivia, además de ser el punto de acceso al Lago Titicaca. el lago navegable más alto del mundo donde podrá visitar la famosa Isla del Sol.  ',385 , 14, 'paquete21.jpg', 30, 1), (22, 'Copacabana, islas flotantes', 'En este tour de día completo, podrá conocer uno de los destinos más representativos de La Paz y Bolivia, inicialmente la población de Copacabana que se destaca por ser un centro histórico cultural en Bolivia donde se encuentra la famosa Basílica de Copacabana y El Mirador del Calvario entre otros, además de ser el punto de acceso al Lago Titicaca el lago navegable más alto del mundo donde podrás visitar las islas flotantes.',490, 13, 'paquete22.jpg', 35, 1), (23, 'Isla de la Luna, Isla del Sol, Copacabana', 'En este tour de 2 días / 1 noche podrás conocer los atractivos más importantes de Copacabana y alrededores. La población de Copacabana se destaca por ser un centro histórico cultural en Bolivia donde se ubican la famosa Basílica de Copacabana y El Mirador del Calvario entre otros, además es el punto de acceso al Lago Titicaca, el lago navegable más alto del mundo, donde se se pueden visitar las dos islas más importantes de este lago, una es la Isla del Sol, cuna de la cultura Inca, y la Isla de la Luna, que simboliza el lado femenino de estas culturas',945 , 14, 'paquete23.jpg', 35, 3), (24, 'CRUCERO EN CATAMARAN, LAGO TITICACA (TITIKAKA)', 'PASEA EN EL LAGO NAVEGABLE MAS ALTO DEL MUNDO A BORDO DE UN CRUCERO CATAMARÁN',398 , 13, 'paquete24.jpg', 50, 3)

